import React, { Component } from 'react';

class SignUp extends Component {
	constructor() {
		super()
		this.state = {
			signUpName: "",
			signUpEmail: "",
			signUpPassword: ""
		} 
	}

	onNameChange = (event) => {
		this.setState({ signUpName: event.target.value })
	}

	onEmailChange = (event) => {
		this.setState({ signUpEmail: event.target.value })
	}

	onPasswordChange = (event) => {
		this.setState({ signUpPassword: event.target.value })
	}

	onSubmitSignIn = () => {
		this.props.onClickSign("signout")
	}

	OnSubmitSignUp = () => {
		fetch('https://immense-sierra-90858.herokuapp.com/signup', {
			method: 'post',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify({
				name: this.state.signUpName,
				email: this.state.signUpEmail,
				password: this.state.signUpPassword
			})
		})
		.then(response => response.json())
		.then(user => {
			if (user.id) {
				this.props.loadUser(user)
				this.props.onClickSign("signin")			
			}
		})
		.catch(console.log);
	}

	render(){
		return(
			<div>
				<nav style={{display: "flex", justifyContent: "flex-end"}}>
					<p onClick={ this.onSubmitSignIn } 
					   className="f3 link dim black underline pa3 pointer" 
					   id="signout">
					   	Sign In
					</p>
				</nav>	
				<div className="center ma4">
					<main className="br3 pa4 black-80 center shadow-1">
					  <form className="measure">
					    <fieldset id="sign_up" 
					    		  className="ba b--transparent ph0 mh0">
					      <legend className="f1 fw6 ph0 mh0">Sign Up</legend>
					      <div className="mt3">
					        <label className="db fw6 lh-copy f6" type="text">Full name</label>
					        <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
					        	   type="text" 
					        	   name="fullName"  
					        	   id="fullName"
					        	   onChange={ this.onNameChange }
					       	/>
					      </div>
					      <div className="mt3">
					        <label className="db fw6 lh-copy f6" type="email">Email</label>
					        <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
					        	   type="email" 
					        	   name="email-address"  
					        	   id="email-address" 
					        	   onChange={ this.onEmailChange }
					       	/>
					      </div>
					      <div className="mv3">
					        <label className="db fw6 lh-copy f6" type="password">Password</label>
					        <input className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
					        	   type="password" 
					        	   name="password"  
					        	   id="password"
					        	   onChange={ this.onPasswordChange }
					        />
					      </div>
					    </fieldset>
					    <div className="">
					      <input onClick={ this.OnSubmitSignUp } 
					      		 className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
					      		 type="button" 
					      		 value="Sign up" 
					      		 id="signup" 
					      />
					    </div>
					  </form>
					</main>
				</div>
			</div>	
		)		
	}
}

export default SignUp;
